import React, { useEffect, useState } from 'react';
import { View, Text, TouchableOpacity, Image } from 'react-native';
import { Circle, Svg } from 'react-native-svg';
import { useNavigation } from '@react-navigation/native';
import styles from './styles/ResultStyle.js';
import { useRoute } from '@react-navigation/native';

const Result = () => {
  const navigation = useNavigation();
  const route = useRoute();
  const imageUrl = route.params?.imageUrl;
  const [result, setResult] = useState('');
  const [scanning, setScanning] = useState(true);

  useEffect(() => {
    const state = navigation.getState().params;
    if (state) {
      const { result } = state;
      setResult(result);

      setTimeout(() => {
        setScanning(false);
      }, 2000);
    }
  }, [navigation]);

  const handleBackToShooting = () => {
    navigation.goBack();
  };

  return (
    <View style={styles.container}>
      <View style={styles.topinfo}>
        <View style={styles.row}>
          <View style={styles.backmenu}>
            <TouchableOpacity style={styles.rectangle} onPress={handleBackToShooting}>
              <Text style={styles.backArrow}>⬅️</Text>
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.mainphoto}>
          <View style={styles.content}>
            <View style={styles.elipse2}>
              <View style={styles.elipse}>
              {imageUrl ? (
                  <Image source={{ uri: imageUrl }} style={styles.camera} />
                ) : null}
              </View>
            </View>
          </View>
        </View>
      </View>
      <View style={styles.bottominfo}>
        <View style={styles.circleChart}>
          {scanning ? (
            <View style={styles.circleChartPercentage}>
              <View style={styles.content}>
                <Text style={styles.scanningText}>Scanning...</Text>
              </View>
            </View>
          ) : (
            <View style={styles.circleChartPercentage}>
              <Svg style={styles.circleChartBackground} viewBox="0 0 50 50">
                <Circle
                  className={`circle-chart__circle circle-chart__circle--${result}`}
                  cx={25}
                  cy={25}
                  r={24}
                  strokeWidth={2}
                />
              </Svg>
              <View style={styles.content}>
                <Text style={styles.resultText}>{result}</Text>
              </View>
            </View>
          )}
        </View>
      </View>
    </View>
  );
};

export default Result;
